    { name: "0", value: 0 },
    { name: "=", value: "=" },
  ];

  const handleBtn = (event) => {
    const btnValue = event.target.value;
    if (
      numArray.includes(btnValue) ||
      leftOperand.indexOf(".") === -1 ||
      !operatorSign.includes(btnValue)
