import { useState } from "react";

function Buttons() {
  const [input, setInput] = useState({});

  const buttons = [
    { name: "C", value: "C" },
    { name: "del", value: "delete" },
    { name: "%", value: "%" },
    { name: "OFF", value: "OFF" },
    { name: "7", value: 7 },
    { name: "8", value: 8 },
    { name: "9", value: 9 },
    { name: "/", value: "/" },
    { name: "4", value: 4 },
    { name: "5", value: 5 },
    { name: "6", value: 6 },
    { name: "*", value: "*" },
    { name: "1", value: 1 },
    { name: "2", value: 2 },
    { name: "3", value: 3 },
    { name: "-", value: "-" },
    { name: "0", value: 0 },
    { name: ".", value: "0" },
    { name: "=", value: "=" },
    { name: "+", value: "+" },
  ];

  const handleClick = (event) => {
    const eventValue = +event.target.value;
    setInput({ ...input, eventValue });
  };

  console.log(input);

  return (
    <div className="btns">
      {buttons.map((btn) => {
        return (
          <button
            key={btn.name}
            value={btn.value}
            className="btn"
            onClick={handleClick}
          >
            {btn.name}
          </button>
        );
      })}
    </div>
  );
}

export default Buttons;
